        presenter.floatingImageLayer.destroy();
        presenter.floatingImageStage.destroy();
        
        for(var i = 0; i < 3; i++) {
            presenter._kinetic.images[i].destroy();
        
            //imageRotateObj
            $(presenter._kinetic.rotateObj[i]).off();
            presenter._kinetic.rotateObj[i].src='';
        
